import React, { Component } from "react";

class GeneralInfo extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: ``,
      lastName: ``,
      email: ``,
      phone: ``,
      address: ``,
      about: ``,
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = (e) => {
    console.log(e.target.value);
    const value = e.target.value;
    console.log(this.state);
    this.setState({
      firstName: value,
    });
  };

  render() {
    return (
      <div className="general-info">
        <input
          type="text"
          className="general-input"
          id="firstName"
          placeholder="First Name"
          onChange={this.handleChange}
        />
        <input type="text" className="general-input" placeholder="Last Name" />
        <input type="text" className="general-input" placeholder="Email" />
        <input
          type="text"
          className="general-input"
          placeholder="Phone Number"
        />
        <input type="text" className="general-input" placeholder="Address" />
        <input
          type="text"
          className="general-input about-me"
          placeholder="About Me"
        />
      </div>
    );
  }
}

export default GeneralInfo;
